import React, { Component }     from 'react';
import $                        from 'jquery';
import jQuery                   from 'jquery';
import axios                    from 'axios';
import swal                     from 'sweetalert';
import moment                   from 'moment';
import _                        from 'underscore';
import IAssureTable             from "../../IAssureTable/IAssureTable.jsx";

import 'bootstrap/js/tab.js';


class CorporateReport extends Component {

    constructor(props) {
        super(props);
        this.state = {
          dateTab   : 'Daily',
          status    : "New",
          bookingStatus    : "",
          selector:{},
          reset:false,
          gmapsLoaded: false,
          company:"",
          search:"",
          stateName:"",
          city:"",
          date:"",
          vendor_name:"",
          todayDate     : '--',
          newDateOne    : '',
          weekdays      : '--',
          monthlyState  : '--',
          fromdate      : '',
          todate        : '',
          booking_id        : '',
          companyArray:[],
          stateArrayList:[],
          cityArrayList:[],
          statusArrayList:[],
          currentYear  : moment().format('YYYY'),
          tableHeading:{
            ComapnyName:"Corporate Name & ID ",
            state:"State",
            city:"City",
            status:"Status",
            state:"State",
            registeredOn:"Registered On",
            employeeCount:"Employees",
            bookingCount:"Bookings",
            completed:"Completed",
            rejected:"Rejected",
            completed:"Completed",
            cancelled:"Cancelled",
            kmTravelled:"Km Travelled",
            totalBill:"Total Bill",
            pendingBillAmount:"Pending Bill Amount",
            settledBillAmount:"Settled Bill Amount",

          },
          RecordsCount:'',
          RecordsTable:[],
          tableObjects : {
            paginationApply : true,
            searchApply     : false,
            downloadApply   : true
          }
        };
        this.handleChange = this.handleChange.bind(this);
    }


    componentDidMount() {
      var today = new Date();
        var dd = today.getDate();
        var mm = today.getMonth()+1; //January is 0!
        var yyyy = today.getFullYear();
        if(dd<10){
            dd='0'+dd;
        }
        if(mm<10){
            mm='0'+mm;
        }
        var today = yyyy+'-'+mm+'-'+dd;

        this.setState({
          todayDate : today,
        },()=>{this.getData()});

        var weeknumber = moment(today).week();
        if(weeknumber<=9){
          weeknumber="0"+weeknumber;
        }
        var yyyy = moment(today).format("YYYY");
        var weekVal = yyyy+"-W"+weeknumber;
        this.setState({
          weekdays:weekVal,
        },()=>{this.getData()});

        var yyyy = moment(today).format("YYYY");
        var monthNum = moment(today).format("MM");
        var currentMonth = yyyy+"-"+monthNum;
        this.setState({
          monthlyState:currentMonth,
        },()=>{this.getData()});

        var fromDt = new Date();
        var toDt = new Date(moment(fromDt).add(1,'d'));

        this.setState({
          fromdate : fromDt,
          todate: toDt
        },()=>{this.getData()})

        var currentYear = moment().format('YYYY');

        this.setState({
          currentYear : currentYear
        },()=>{this.getData()})

