	redirectTo(event)
    {
    	localStorage.setItem("entityTypeList",this.props.entityType)
    	console.log("window.location.pathname",window.location.pathname)
    	this.props.history.push("/"+this.props.entityType+"/"+this.state.pathname+"/master")
    }
    deleteDriver(event){
		event.preventDefault();
		this.setState({deleteID: event.currentTarget.getAttribute('data-id')})
		$('#deleteModal').show();
    }
    restoreDriver(event){
    	event.preventDefault();
		var driverID = event.currentTarget.getAttribute('data-id')
      	var details={
			driverID    : driverID,
            updatedBy   : localStorage.getItem("user_ID")
		}
      	axios.patch("/api/personmaster/patch/restore_driver", details)
            .then((response)=>{
              	this.getpersons();
              	this.deletedDriver(); 

           		if (response.data) {
           			swal({
           				title : " ",
	                    text : "Record is restored successfully.",
	                 });
           		}	else{
           			swal({
           				title : " ",
	                    text : "Failed to restore.",
	                  });
           		}

            })
            .catch((error)=>{
            })
    }
    confirmDelete(event){
    	event.preventDefault();
    	axios.get("/api/personmaster/get/one/"+this.state.deleteID)
        .then((response)=>{
          this.setState({
              "personID" : response.data.userId,
          },()=>{
      		axios.delete("/api/personmaster/delete/"+this.state.deleteID)
            .then((response)=>{
              	$('#deleteModal').hide();  
              	this.deletedDriver(); 
            	axios.delete("/api/users/delete/"+this.state.personID)
			        .then((response)=>{
			        })
			        .catch((error)=>{
			        })
           		if (response.data) {
           			swal({
	                    title : " ",
	                    text :  "Record is deleted successfully."
	                  });
           		}	else{
           			swal({
	                    title : " ",
	                    text  : "Failed to delete.",
	                  });
           		}
              	this.getpersons();
              	this.hideForm();
            })
            .catch((error)=>{
            })
          });

        })
        .catch((error)=>{
        })
    }
