	handleEventChange(event) {
		this.setState({template:""})
		const { name, value } = event.target;
		let formerrors = this.state.formerrors;
		var str = value ; 
		var finalStr = str.split("-");
		var event = finalStr[0];
		var template = finalStr[1];
		this.setState({
			formerrors,
			[name]: value,
			event: event,
			template: template
		},()=>{this.getTokens(event)});
	}

